import React, { useState, useEffect } from "react";

function App() {
  const url = "http://localhost:8080/showimage";
  const [imgSrc, setImgSrc] = useState(null);

  useEffect(() => {
    // Defines the function to fetch the image
    const fetchImage = () => {
      fetch(url)
        .then((response) => {
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          return response.blob();
        })
        .then((imageBlob) => {
          const imageObjectURL = URL.createObjectURL(imageBlob);
          setImgSrc(imageObjectURL);
        })
        .catch((error) => {
          console.error("Error fetching the image:", error);
          setImgSrc(null); // Reset the image source if there is an error
        });
    };

    // Call the fetch function immediately on mount
    fetchImage();

    // Set up the interval to fetch the image every 3000 milliseconds
    const intervalId = setInterval(fetchImage, 5000);

    // Clear the interval on component unmount
    return () => clearInterval(intervalId);
  }, [url]); // url is stable and constant, but adding it to the dependency array for completeness

  return (
    <div className="App">
      {imgSrc ? (
        <div
          style={{
            width: "100%",
            height: 1660,
            textAlign: "center",
          }}
        >
          <img
            src={imgSrc}
            alt="Dynamic from server"
            style={{ verticalAlign: "middle", maxHeight: "90%" }} // 调整了图片样式
          />
          {!imgSrc && <p>Loading...</p>}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default App;
